import { Component, OnInit } from '@angular/core';
import { Observable, Subject, map, filter } from 'rxjs';
import { Plane } from '../models/plane.model';
import { PlaneService } from '../services/plane.service';


@Component({
  selector: 'app-composant-list-avion',
  templateUrl: './composant-list-avion.component.html',
  styleUrls: ['./composant-list-avion.component.css']
})
export class ComposantListAvionComponent implements OnInit {
  searchText: any;
  plane$ !: Observable<Plane[]>;
  planedefault$ !: Observable<Plane[]>;
  private destroy$!: Subject<boolean>;

  constructor(private planeService: PlaneService) { }

  ngOnInit(): void {
    this.planedefault$ = this.planeService.getAllPlanes();
    this.plane$ = this.planedefault$
    this.destroy$ = new Subject<boolean>();
  }

  filter(name) {
    this.plane$.subscribe(result => {console.log(result.length)});
    this.plane$= this.plane$.pipe(
      map(projects =>projects.filter(proj => proj.airplane.includes(name))
      )
    );
    this.plane$.subscribe(result => {console.log(result.length)});
  }




  ngOnDestroy(): void {
    this.destroy$.next(true);
  }

}

<div class="container">
    <div class="col-md-12">

            <div class="search-name">
                <input (ngModelChange)="filter($event)" class="form-control" type="text" name="search" [(ngModel)]="searchText" autocomplete="on" placeholder=" SEARCH  ">
            </div>
            <app-composant-afficher-avion *ngFor="let planeElement of plane$  | async" [plane]="planeElement" >
            </app-composant-afficher-avion>
        </div>

</div>
